import React, {useContext, useEffect} from 'react'
import useRequestData from '../../hooks/useRequestData'
import RestaurantCard from './RestaurantCard'
import useForm from '../../hooks/useForm'
import { Box } from '@material-ui/core'
import { TextField } from '@material-ui/core'
import Footer from '../../Footer/Footer'
import useProtectedPage from '../../hooks/useProtectedPage'
import GlobalStateContext from '../../Global/GlobalStateContext'
import Header from '../../Header/Header'
import { useHistory } from 'react-router'
import styled from 'styled-components'

const Div = styled.div`
display: flex;
justify-content: center;
flex-direction: column;
align-items: center;
`

const FeedPage = () => {

    useProtectedPage()
    const history = useHistory()

    const {isClicked, setIsClicked} = useContext(GlobalStateContext)

    const [form, onChangeInput] = useForm({
        search: ''
    })

    const renderOptions = () => {
        setIsClicked(true)
    }

    const renderCards = () => {
        setIsClicked(false)
    }

    useEffect(() => {
        renderCards()
    }, [form])

    const makeSearch = <p>Fa√ßa sua busca</p>

    return (
        <Div>
            <Header title={"Rappi4"} logout={history}/>
            {isClicked ? <button onClick={() => renderCards()}>Cancelar</button> : null}
            <Box
                component="form"
                sx={{
                    '& > :not(style)': { m: 1, width: '80vw' },
                }}
                noValidate
                autoComplete="off"
            >
                <TextField 
                    id="outlined-basic"
                    name='search'
                    value={form.search}
                    onChange={onChangeInput}
                    label="Busca" 
                    variant="outlined" 
                    onClick={form.search ? null : () => renderOptions()}
                />
            </Box>
            {isClicked ? makeSearch : <RestaurantCard
                search={form.search}
            />}
            <Footer feed = {true}/>
        </Div>
    )
}

export default FeedPage;
